import React, { Component } from 'react'
import LoadImageFile from './LoadImageFile';

export default class PhotoGallery extends Component {
  state = {
    wantToAdd: false,
    imgArr: []
  }
  handleAdd = () => {
    this.setState({
      wantToAdd: !this.state.wantToAdd
    })
  }
  componentDidMount = () => {
    const images = [...images, JSON.parse(localStorage.getItem('img'))]
    console.log(images)
    images.map(image => {
      // const imgSrc = window.URL.createObjectURL(image.imgFile)
      this.setState({
        imgArr: image
      })

    })
  }

  render() {
    const { imgArr } = this.state;
    return (
      <div>
        <div className="header-container">
            <h1 className="header-container_heading">Photo Library</h1>
            <div className="add_icon" onClick={this.handleAdd}><span>+</span></div>
        </div>
        <form className="search-box">
            <input type="text" className="search-input" placeholder="Search Text" />
        </form>
        {
          imgArr.map(img => {
            const imgSrc = window.URL.createObjectURL(img.imgFile);
            console.log(imgSrc)
          })
        }
        <LoadImageFile />
      </div>
    )
  }
}
