import React, { Component } from 'react';
import { connect } from 'react-redux';
import { storeImage } from '../acttions/actionCreators';

class LoadImageFile extends Component {
  state = {
    imgFile: [],
    imgURL: '',
    imgInfo: ''
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
    }
    handleImgURL = (e) => {
      console.log(e.target.files[0], "image in handleImageURL")

      this.setState({
        imgFile: e.target.files
      })
    }

    handleSubmit = (e) => {
      e.preventDefault();
      this.props.dispatch(storeImage(this.state))
      // localStorage.setItem("img", [...JSON.stringify(JSON.parse(localStorage.getItem("img")),{
      //   imgFile: this.state.imgFile,
      //   imgURL: this.state.imgURL,
      //   imgInfo: this.state.imgInfo
      // })])
    }
  render() {
    console.log(this.state.imgFile[0])
    const src = URL.createObjectURL(new Blob(this.state.imgFile, {type: "application/zip"}));
    console.log(src)
    return (
      <div className="modale-container">
        <header className="modale-header">
          <h3>Add Photo</h3>
          <div className="remove-btn">x</div>
        </header>
        <div>
          <form>
            <input type="file" name="imgFile" onChange={this.handleImgURL} multiple="multiple" />
          </form>
          <form>
            <input type="text" name="imgURL" onChange={this.handleChange} />
          </form>
        </div>
        <form onSubmit={this.handleSubmit}>
          <input type="text" placeholder="Type Something, hit enter" name="imgInfo" onChange={this.handleChange} />
          <button type="submit">Upload</button>
        </form>
          <img src={src.slice(5)} id="img-link" />

      </div>
    )
  }
}

export default connect()(LoadImageFile)
