import React, { Component } from 'react';
import { connect } from 'react-redux';
import LoadImageFile from './LoadImageFile';

class PhotoGallery extends Component {
  state = {
    wantToAdd: false,
    imgArr: [],
    imgSrcArr: []
  }
  handleAdd = () => {
    this.setState({
      wantToAdd: !this.state.wantToAdd
    })
  }
  componentWillMount = () => {
   const images = JSON.parse(localStorage.getItem("img"));
   console.log(images)
   images.forEach(img => {
     this.setState({
       imgSrcArr: [...this.state.imgSrcArr, img.imgFile]
     })
   })
  }

  render() {
    // const src = URL.createObjectURL(new Blob(this.state.imgSrcArr, {type: "application/zip"}));
    return (
      <div>
        <div className="header-container">
            <h1 className="header-container_heading">Photo Library</h1>
            <div className="add_icon" onClick={this.handleAdd}><span>+</span></div>
        </div>
        <form className="search-box">
            <input type="text" className="search-input" placeholder="Search Text" />
        </form>
        {
          images && images.map(img => <img src={img.imgFile} alt="gallery" />)
        }
        {/* <img src={this.state.imgSrcArr[0]} id="img-link" /> */}
        <LoadImageFile />
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {

  }
} 

export default connect(mapStateToProps)(PhotoGallery)
