const initState = {
    images: [],
    imgSrc: [],
}

export default function rootReducer(state= initState, action) {
    switch(action.type) {
        case 'STORE_IMAGE': {
            return {
                images: action.data,
                imgSrc: [...state.imgSrc, action.data.imgFile]
            }
        }
        default:
        return state;
    }
}
