import React, { Component } from 'react'
import LoadImageFile from './LoadImageFile';

export default class PhotoGallery extends Component {
  state = {
    wantToAdd: false
  }
  handleAdd = () => {
    this.setState({
      wantToAdd: !this.state.wantToAdd
    })
  }
  componentDidMount = () => {
    const imges = localStorage.getItem('img')
    console.log(images)
  }
  render() {
    return (
      <div>
        <div className="header-container">
            <h1 className="header-container_heading">Photo Library</h1>
            <div className="add_icon" onClick={this.handleAdd}><span>+</span></div>
        </div>
        <form className="search-box">
            <input type="text" className="search-input" placeholder="Search Text" />
        </form>
        <LoadImageFile />
      </div>
    )
  }
}
