import React, { Component } from 'react'
import LoadImageFile from './LoadImageFile';

export default class PhotoGallery extends Component {
  state = {
    wantToAdd: false,
    imgArr: [],
    imgSrcArr: []
  }
  handleAdd = () => {
    this.setState({
      wantToAdd: !this.state.wantToAdd
    })
  }
  componentWillMount = () => {
    const images = []
    images.push(JSON.parse(localStorage.getItem('img')))
    console.log(images[0])
    this.setState({
      imgArr: images[0], 
    })

    const srcArr = [...this.state.imgSrcArr]

    images[0].forEach((image,i) => {
      srcArr.push(image.imgFile)
    })
    this.setState({
      imgSrcArr: srcArr 
    })
  }

  render() {
    const src = window.URL.createObjectURL(new Blob(this.state.imgSrcArr, {type: "application/zip"}));
    return (
      <div>
        <div className="header-container">
            <h1 className="header-container_heading">Photo Library</h1>
            <div className="add_icon" onClick={this.handleAdd}><span>+</span></div>
        </div>
        <form className="search-box">
            <input type="text" className="search-input" placeholder="Search Text" />
        </form>
        <img src={src.slice(5)} id="img-link" />
        <LoadImageFile />
      </div>
    )
  }
}
