import React, { Component } from 'react'

export default class LoadImageFile extends Component {
  state = {
    imgFile: [],
    imgURL: '',
    imgInfo: ''
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
    }
    handleImgURL = (e) => {
      this.setState({
        imgFile: e.target.files
      })
    }

    handleSubmit = (e) => {
      e.preventDefault();
      localStorage.setItem("img", [...JSON.stringify(JSON.parse(localStorage.getItem("img")),{
        imgFile: this.state.imgFile,
        imgURL: this.state.imgURL,
        imgInfo: this.state.imgInfo
      })])
    }
  render() {
    return (
      <div className="modale-container">
        <header className="modale-header">
          <h3>Add Photo</h3>
          <div className="remove-btn">x</div>
        </header>
        <div>
          <form>
            <input type="file" name="imgFile" onChange={this.handleImgURL} multiple />
          </form>
          <form>
            <input type="text" name="imgURL" onChange={this.handleChange} />
          </form>
        </div>
        <form onSubmit={this.handleSubmit}>
          <input type="text" placeholder="Type Something, hit enter" name="imgInfo" onChange={this.handleChange} />
          <button type="submit">Upload</button>
        </form>
      </div>
    )
  }
}
